mapper.update:
UPDATE TBL_BOARD
SET BRDHIT = BRDHIT + 1
WHERE BRDNO = #{brdNo}

UPDATE TBL_BOARD
SET  BRDTITLE=#{brdTitle}, BRDMEMO = #{brdMemo}, BRDWRITER = #{brdWriter}
WHERE BRDNO = #{brdNo} AND BRDDELETEFLAG = 'N'

UPDATE TBL_BOARD
SET BRDDELETEFLAG = 'Y'
WHERE BRDNO = #{brdNo}

mapper.delete:
DELETE
FROM TBL_BOARDFILE
WHERE FILENO IN (

    ${item}

)
